"use strict";
$(function () { 
    let ListAnimate = this.querySelectorAll(".animate");
    let x = this.querySelector(".info ");
    CheckArray();
     window.onscroll = () => {
         CheckArray();
         
    }; 
  function CheckArray() {
        if (ListAnimate.length > 0) {
            ListAnimate.forEach((e) => {
                let windowHeight = window.innerHeight;
                let windoScroll = window.pageYOffset;
                let element = e.offsetTop;
                let elementOffestHeight = e.offsetHeight;
                let result = (element + elementOffestHeight) - windowHeight;
                let status = result > 0 ? result : 0;
                if (windoScroll >= status) {
                    e.classList.add(e.dataset.animate);
                    e.style.visibility = 'visible';
                }

                if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight) {
                    ListAnimate = [];
                }
            });

        }

    }

});